	runBusRuleCode();

function runBusRuleCode(){
	var x = 0;
	var ge =  new GlideRecord('x_402053_skill_eva_skill_assetment');
	var ga = new GlideRecord('x_402053_skill_eva_submission');
	ge.addQuery('proficiency_level','!=','none');
	ge.addQuery('submission',current.sys_id);
	ge.query();
	while(ge.next())
		{

		if(ge.getValue('proficiency_level') == 'concep')
			{
			x = x+1;
		}
		else if(ge.getValue('proficiency_level') == 'exp')
			{
			x = x+2;
		}
		else if(ge.getValue('proficiency_level') == 'expert')
			{
			x = x+3;
		}
		else if(ge.getValue('proficiency_level') == 'guru')
			{
			x = x+4;
		}

	}
	current.valid = 'true';
	current.score = x;
	current.state = 'submit';
	current.update();
	//	action.setRedirectURL(current); // sau khi update thi` quay tro lai cai form current ma` ta dang lam, neu ko co lenh nay thi no se hien len table list thay vi` form
// 	if(x >= 15 && x < 25)
