// 		{
// 		current.level = 'fe3679f0db084010ff3f5385ca961903';
// 		current.update();
// 	}
// 	else if(x >= 30 && x < 45)
// 		{
// 		current.level = '7a46b1f0db084010ff3f5385ca9619b0';
// 		current.update();
// 	}
// 	else if(x >= 45 && x <60)
