	var test = new GlideRecord('x_402053_skill_eva_level');
	test.query();
	while(test.next())
		{

			if(current.score >= test.min_score)
				{

					current.level=test.sys_id;
					current.update();
				}
		}
	action.setRedirectURL(current);
}
